mapper.select:
select * from (
select rowNum rNum, b_no, username, b_title, b_hit, b_ref, b_step,
b_level, b_date
from (select * from golforboard	



			b_title like #{find_name} 


			b_cont like #{find_name}


order by b_ref desc, b_level asc)) where rNum >=#{startrow} and rNum <=#{endrow}

select count(b_no) from board






 board_title like #{find_name} 


 board_cont like #{find_name}

mapper.update:
update golforboard set b_level=b_level+1
where b_ref=#{b_ref} 
and b_level>#{b_level}

update golforboard set username=#{username},
b_title=#{b_title},b_cont=#{b_cont}
where b_no=#{b_no}
